/**
 * Ref ÏÇ¨Ïö© ÏòàÏ†ú: ButtonÏóê refÎ•º Ï†ÑÎã¨ÌïòÏó¨ DOM ÏöîÏÜåÏóê ÏßÅÏ†ë Ï†ëÍ∑ºÌï©ÎãàÎã§.
 * Ïù¥ ÏòàÏ†úÎäî refÎ•º ÌÜµÌïú focus Ï†úÏñ¥Î•º Î≥¥Ïó¨Ï§çÎãàÎã§.
 */
export const WithRef: Story = {
  render: () => {
    // üéØ Î™©Ï†Å: HTMLButtonElementÏóê ÎåÄÌïú refÎ•º ÏÉùÏÑ±ÌïòÏó¨ focus() Î©îÏÑúÎìú Ï†ëÍ∑º
    const buttonRef = useRef<HTMLButtonElement>(null);

    return (
      <div className="flex flex-col gap-4">
        <Button ref={buttonRef}>Target Button</Button>
        <Button variant="outline" onClick={() => buttonRef.current?.focus()}>
          Focus Button Above
        </Button>
      </div>
    );
  },
  play: async ({ canvasElement }) => {
    // üéØ Î™©Ï†Å: play functionÏùÑ ÌÜµÌï¥ ref ÎèôÏûëÏùÑ ÏûêÎèôÏúºÎ°ú ÌÖåÏä§Ìä∏
    const canvas = within(canvasElement);
    const buttons = canvas.getAllByRole("button");

    // "Focus Button Above" Î≤ÑÌäº ÌÅ¥Î¶≠ÌïòÏó¨ Ï≤´ Î≤àÏß∏ Î≤ÑÌäºÏóê focus Ìä∏Î¶¨Í±∞
    await userEvent.click(buttons[1]);

    // Ï≤´ Î≤àÏß∏ Î≤ÑÌäºÏù¥ Ìè¨Ïª§Ïä§Î•º Î∞õÏïòÎäîÏßÄ Í≤ÄÏ¶ù
    await expect(buttons[0]).toHaveFocus();
  },
};

"use client"

import type { Meta, StoryObj } from "@storybook/nextjs-vite";
import * as React from "react";

import { Calendar } from "@/components/ui/calendar";

export function CalendarDemo() {
  const [date, setDate] = React.useState<Date | undefined>(new Date())

  return (
    <Calendar
      mode="single"
      selected={date}
      onSelect={setDate}
      className="rounded-md border"
    />
  )
}

/**
 * A date field component that allows users to enter and edit date.
 */
const meta = {
  title: "ui/Calendar",
  component: Calendar,
  tags: ["autodocs"],
  parameters: {
    layout: "centered",
  },
  excludeStories: /.*Demo$/,
  render: () => <CalendarDemo />
} satisfies Meta<typeof Calendar>;

export default meta;

type Story = StoryObj<typeof meta>;

/**
 * The default form of the calendar.
 */
export const Default: Story = {};

/**
 * Calendar with form example.
 */
export const Form: Story = {
  render: () => {
    const [date, setDate] = React.useState<Date | undefined>()

    return (
      <form className="space-y-8">
        <Calendar
          mode="single"
          selected={date}
          onSelect={setDate}
          className="rounded-md border"
        />
      </form>
    )
  },
};

import type { Meta, StoryObj } from "@storybook/nextjs-vite";
import { useRef } from "react";

import { Button } from "@/components/ui/button";
import {
  Card,
  CardAction,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { expect, userEvent, within } from "storybook/test";

export function CardDemo() {
  return (
    <Card className="w-full max-w-sm">
      <CardHeader>
        <CardTitle>Login to your account</CardTitle>
        <CardDescription>
          Enter your email below to login to your account
        </CardDescription>
        <CardAction>
          <Button variant="link">Sign Up</Button>
        </CardAction>
      </CardHeader>
      <CardContent>
        <form>
          <div className="flex flex-col gap-6">
            <div className="grid gap-2">
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                type="email"
                placeholder="m@example.com"
                required
              />
            </div>
