/**
 * Ref ÏÇ¨Ïö© ÏòàÏ†ú: FormControlÏùÑ ÌÜµÌï¥ InputÏóê refÎ•º Ï†ÑÎã¨ÌïòÏó¨ DOM ÏöîÏÜåÏóê Ï†ëÍ∑ºÌï©ÎãàÎã§.
 * react-hook-formÏùò register ÎåÄÏã† refÎ•º Î™ÖÏãúÏ†ÅÏúºÎ°ú Ï†ÑÎã¨ÌïòÎäî Î∞©Î≤ïÏùÑ Î≥¥Ïó¨Ï§çÎãàÎã§.
 */
export const WithRef: Story = {
  render: () => {
    // üéØ Î™©Ï†Å: react-hook-formÍ≥º Ìï®Íªò refÎ•º Î™ÖÏãúÏ†ÅÏúºÎ°ú Í¥ÄÎ¶¨
    const inputRef = useRef<HTMLInputElement>(null);

    const formSchema = z.object({
      email: z.string().email({ message: "Invalid email address." }),
      password: z
        .string()
        .min(8, { message: "Password must be at least 8 characters." }),
    });

    const form = useForm<z.infer<typeof formSchema>>({
      resolver: zodResolver(formSchema),
      defaultValues: {
        email: "",
        password: "",
      },
    });

    function onSubmit(values: z.infer<typeof formSchema>) {
      console.log("Form submitted:", values);
    }

    return (
      <div className="flex flex-col gap-4">
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="email"
              render={({ field: { ref, ...fieldWithoutRef } }) => (
                <FormItem>
                  <FormLabel>Email</FormLabel>
                  <FormControl>
                    <Input
                      ref={(e) => {
                        ref(e);
                        inputRef.current = e;
                      }}
                      type="email"
                      placeholder="example@email.com"
                      {...fieldWithoutRef}
                    />
                  </FormControl>
                  <FormDescription>
                    Your email address for account access.
                  </FormDescription>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="password"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Password</FormLabel>
                  <FormControl>
                    <Input
                      type="password"
                      placeholder="Enter password"
                      {...field}
                    />
                  </FormControl>
                  <FormDescription>
                    Must be at least 8 characters long.
                  </FormDescription>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex gap-2">
              <Button type="submit">Submit</Button>
              <Button
                type="button"
                variant="outline"
                onClick={() => inputRef.current?.focus()}
              >
                Focus Email Input
              </Button>
            </div>
          </form>
        </Form>
      </div>
    );
  },
  play: async ({ canvasElement }) => {
    // üéØ Î™©Ï†Å: play functionÏùÑ ÌÜµÌï¥ form refÏôÄ validationÏùÑ ÌÖåÏä§Ìä∏
    const canvas = within(canvasElement);

    // "Focus Email Input" Î≤ÑÌäºÏúºÎ°ú email inputÏóê focus
    const focusButton = canvas.getByRole("button", {
      name: "Focus Email Input",
    });
    await userEvent.click(focusButton);

    // Email inputÏù¥ Ìè¨Ïª§Ïä§ÎêòÏóàÎäîÏßÄ ÌôïÏù∏
    const emailInput = canvas.getByLabelText("Email");
    await expect(emailInput).toHaveFocus();

    // ÏûòÎ™ªÎêú Ïù¥Î©îÏùº ÏûÖÎ†•
    await userEvent.type(emailInput, "invalid-email");

    // Submit Î≤ÑÌäº ÌÅ¥Î¶≠
    const submitButton = canvas.getByRole("button", { name: "Submit" });
    await userEvent.click(submitButton);

    // Validation ÏóêÎü¨ Î©îÏãúÏßÄ ÌôïÏù∏
    const errorMessage = await canvas.findByText("Invalid email address.");
    await expect(errorMessage).toBeVisible();
  },
};

