type Color = {
  name: string;
  value: string;
  description?: string;
};

// üé® shadcn ÌÜ†ÌÅ∞ ÏÉâÏÉÅ ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏ (Îã§ÌÅ¨Î™®Îìú Í∞êÏßÄ Ìè¨Ìï®)
const ColorTile = ({ value }: Pick<Color, "value">) => {
  const [colorValue, setColorValue] = useState("");

  useEffect(() => {
    // üåì CSS Î≥ÄÏàò Í∞íÏùÑ ÏùΩÏñ¥ÏÑú ÌëúÏãúÏö©ÏúºÎ°úÎßå ÏÇ¨Ïö©
    const updateColor = () => {
      const styles = getComputedStyle(document.documentElement);
      const cssValue = styles.getPropertyValue(value);
      setColorValue(cssValue.trim());
    };

    // Ï¥àÍ∏∞ Î°úÎìú Ïãú ÏÉâÏÉÅ ÏùΩÍ∏∞
    updateColor();

    // üîÑ MutationObserverÎ°ú Îã§ÌÅ¨Î™®Îìú Ïã§ÏãúÍ∞Ñ Í∞êÏßÄ
    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (
          mutation.type === "attributes" &&
          mutation.attributeName === "class"
        ) {
          updateColor();
        }
      });
    });

    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    });

    return () => {
      observer.disconnect();
    };
  }, [value]);

  // üé® Tailwind v4: CSS Î≥ÄÏàò Í∞íÏù¥ Ïù¥ÎØ∏ "hsl(0 0% 100%)" ÌòïÏãù (hsl() Ìè¨Ìï®)
  // React Ïù∏ÎùºÏù∏ Ïä§ÌÉÄÏùº Ìò∏ÌôòÏùÑ ÏúÑÌï¥ Í≥µÎ∞±ÏùÑ ÏâºÌëúÎ°ú Î≥ÄÌôò
  const hslValue = colorValue.replace(/hsl\(([^)]+)\)/, (match, values) => {
    return `hsl(${values.replace(/\s+/g, ", ")})`;
  });

  return (
    <div className="flex flex-col items-center gap-2">
      <div
        className="size-20 rounded-md border"
        style={{ backgroundColor: hslValue || "transparent" }}
      />
      <p className="text-center text-xs opacity-70">{value}</p>
      <p className="text-center font-mono text-xs">{colorValue}</p>
    </div>
  );
};

