/**
 * üé® Tailwind Palette: Tailwind CSS Ï†ÑÏ≤¥ ÏÉâÏÉÅ ÌåîÎ†àÌä∏
 *
 * Tailwind CSSÏùò 22Í∞ú ÏÉâÏÉÅ ÏãúÏä§ÌÖú (Í∞Å 11Í∞ú shade: 50-950)Í≥º shadcn ÌÜ†ÌÅ∞ÏùÑ Ìï®Íªò Î≥¥Ïó¨Ï§çÎãàÎã§.
 * Îã§ÌÅ¨Î™®Îìú Ï†ÑÌôò Ïãú shadcn ÌÜ†ÌÅ∞ Í∞íÏù¥ Ïã§ÏãúÍ∞ÑÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏Îê©ÎãàÎã§.
 */
export const TailwindPalette: Story = {
  render: () => {
    const [tokens, setTokens] = useState<Record<string, string>>({});
    const [isDarkMode, setIsDarkMode] = useState(false);

    // üåì CSS Î≥ÄÏàò Í∞íÏùÑ Í∞ÄÏ†∏Ïò§Îäî Ìï®Ïàò (Îã§ÌÅ¨Î™®Îìú Ïã§ÏãúÍ∞Ñ Í∞êÏßÄ)
    const updateTokens = () => {
      const styles = getComputedStyle(document.documentElement);
      const tokenList = [
        "--background",
        "--foreground",
        "--primary",
        "--primary-foreground",
        "--secondary",
        "--secondary-foreground",
        "--muted",
        "--muted-foreground",
        "--accent",
        "--accent-foreground",
        "--destructive",
        "--destructive-foreground",
        "--border",
        "--input",
        "--ring",
        "--card",
        "--card-foreground",
        "--popover",
        "--popover-foreground",
        "--chart-1",
        "--chart-2",
        "--chart-3",
        "--chart-4",
        "--chart-5",
        "--sidebar",
        "--sidebar-foreground",
        "--sidebar-primary",
        "--sidebar-primary-foreground",
        "--sidebar-accent",
        "--sidebar-accent-foreground",
        "--sidebar-border",
        "--sidebar-ring",
      ];
      const newValues: Record<string, string> = {};
      tokenList.forEach((token) => {
        newValues[token] = styles.getPropertyValue(token).trim();
      });
      setTokens(newValues);
      setIsDarkMode(document.documentElement.classList.contains("dark"));
    };

    // üîÑ Ï¥àÍ∏∞ Î°úÎìú Î∞è Îã§ÌÅ¨Î™®Îìú Î≥ÄÍ≤Ω Í∞êÏßÄ
    useEffect(() => {
      updateTokens();

      // MutationObserverÎ°ú ÌÅ¥ÎûòÏä§ Î≥ÄÍ≤Ω Í∞êÏßÄ
      const observer = new MutationObserver((mutations) => {
        mutations.forEach((mutation) => {
          if (
            mutation.type === "attributes" &&
            mutation.attributeName === "class"
          ) {
            updateTokens();
          }
        });
      });

      observer.observe(document.documentElement, {
        attributes: true,
        attributeFilter: ["class"],
      });

      return () => {
        observer.disconnect();
      };
    }, []);

    // Îã®Ïùº Ïª¨Îü¨ Î∞ïÏä§ Ïª¥Ìè¨ÎÑåÌä∏
    const ColorBox = ({ name, value }: { name: string; value: string }) => (
      <div key={name} style={{ textAlign: "center" }}>
        <div
          style={{
            background: value || "transparent",
            width: "100%",
            height: "60px",
            borderRadius: "8px",
            border: isDarkMode ? "1px solid #333" : "1px solid #e5e5e5",
          }}
        />
        <p style={{ fontSize: "12px", marginTop: "4px" }}>{name}</p>
        <code style={{ fontSize: "10px" }}>{value || "-"}</code>
      </div>
    );

    // Tailwind ÌåîÎ†àÌä∏ Î†åÎçîÎßÅ Ìï®Ïàò
    const renderTailwindColors = (
      colors: Record<string, string | Record<string, string>>,
      parent = "",
    ) => {
      return Object.entries(colors).map(([name, value]) => {
        const key = parent ? `${parent}-${name}` : name;
        if (typeof value === "string") {
          return <ColorBox key={key} name={key} value={value} />;
        }
        if (typeof value === "object") {
          return (
            <div key={key} style={{ marginBottom: "2rem", width: "100%" }}>
              <h3
                style={{ marginBottom: "0.5rem", textTransform: "capitalize" }}
              >
                {name}
              </h3>
              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: "repeat(11, 1fr)",
                  gap: "0.5rem",
                }}
              >
                {renderTailwindColors(value, name)}
              </div>
            </div>
          );
        }
        return null;
      });
    };

    // Tailwind ÌÅ¥ÎûòÏä§ Îß§ÌïëÏúºÎ°ú shadcn ÌÜ†ÌÅ∞ ÌëúÏãú
    const tokenClassMap: Record<string, string> = {
      "--background": "bg-background",
      "--foreground": "bg-foreground",
      "--primary": "bg-primary",
      "--primary-foreground": "bg-primary-foreground",
      "--secondary": "bg-secondary",
      "--secondary-foreground": "bg-secondary-foreground",
      "--muted": "bg-muted",
      "--muted-foreground": "bg-muted-foreground",
      "--accent": "bg-accent",
      "--accent-foreground": "bg-accent-foreground",
      "--destructive": "bg-destructive",
      "--destructive-foreground": "bg-destructive-foreground",
      "--border": "bg-border",
      "--input": "bg-input",
      "--ring": "bg-ring",
      "--card": "bg-card",
      "--card-foreground": "bg-card-foreground",
      "--popover": "bg-popover",
      "--popover-foreground": "bg-popover-foreground",
      "--chart-1": "bg-chart-1",
      "--chart-2": "bg-chart-2",
      "--chart-3": "bg-chart-3",
      "--chart-4": "bg-chart-4",
      "--chart-5": "bg-chart-5",
      "--sidebar": "bg-[hsl(var(--sidebar))]",
      "--sidebar-foreground": "bg-[hsl(var(--sidebar-foreground))]",
      "--sidebar-primary": "bg-[hsl(var(--sidebar-primary))]",
      "--sidebar-primary-foreground":
        "bg-[hsl(var(--sidebar-primary-foreground))]",
      "--sidebar-accent": "bg-[hsl(var(--sidebar-accent))]",
      "--sidebar-accent-foreground":
        "bg-[hsl(var(--sidebar-accent-foreground))]",
      "--sidebar-border": "bg-[hsl(var(--sidebar-border))]",
      "--sidebar-ring": "bg-[hsl(var(--sidebar-ring))]",
    };

    return (
      <div>
        {/* ÌòÑÏû¨ ÌÖåÎßà ÌëúÏãú */}
        <div
          style={{
            marginBottom: "1rem",
            padding: "0.5rem",
            background: isDarkMode ? "#333" : "#f5f5f5",
            borderRadius: "4px",
          }}
        >
          <strong>Current Theme:</strong>{" "}
          {isDarkMode ? "Dark Mode" : "Light Mode"}
        </div>

        {/* shadcn ÌÜ†ÌÅ∞ */}
        <h2 style={{ marginBottom: "1rem" }}>Shadcn Design Tokens</h2>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(6, 1fr)",
            gap: "1rem",
            marginBottom: "2rem",
          }}
        >
          {Object.entries(tokenClassMap).map(([token, className]) => {
            const value = tokens[token];
            return (
              <div key={token} style={{ textAlign: "center" }}>
                <div
                  className={className}
                  style={{
                    width: "100%",
                    height: "60px",
                    borderRadius: "8px",
                    border:
                      token === "--card" || token === "--background"
                        ? isDarkMode
                          ? "2px solid #666"
                          : "2px solid #ccc"
                        : isDarkMode
                          ? "1px solid #333"
                          : "1px solid #e5e5e5",
                  }}
                />
                <p style={{ fontSize: "12px", marginTop: "4px" }}>{token}</p>
                <code style={{ fontSize: "10px" }}>
                  {value || "loading..."}
                </code>
              </div>
            );
          })}
        </div>

        {/* Tailwind ÌåîÎ†àÌä∏ */}
        <h2 style={{ marginBottom: "1rem" }}>Tailwind Color Palette</h2>
        {renderTailwindColors(tailwindColors)}
      </div>
    );
  },
};

