  brandName?: string;
  /** íƒœê·¸ë¼ì¸ ë˜ëŠ” ì„œë¸Œ í…ìŠ¤íŠ¸ */
  tagline?: string;
  /** ë¡œë”© ì§„í–‰ë¥  (0-100) - ì„¤ì • ì‹œ í”„ë¡œê·¸ë ˆìŠ¤ ë°” í‘œì‹œ */
  progress?: number;
  /** ëª¨ë‹¬ì´ ì™„ì „íˆ ë‹«íŒ í›„ í˜¸ì¶œë˜ëŠ” ì½œë°± */
  onComplete?: () => void;
