  onComplete,
  className,
}: SplashModalProps) {
  // ğŸ¯ ëª¨ë‹¬ ê°€ì‹œì„± ìƒíƒœ ê´€ë¦¬
  const [isVisible, setIsVisible] = React.useState(true);
  // ğŸ¯ ë‹«í˜ ì• ë‹ˆë©”ì´ì…˜ íŠ¸ë¦¬ê±° ìƒíƒœ
  const [isClosing, setIsClosing] = React.useState(false);

  // ğŸ¯ ë¡œë”© ì™„ë£Œ ì‹œ ë‹«í˜ ì• ë‹ˆë©”ì´ì…˜ ì‹œì‘
  React.useEffect(() => {
    if (!isLoading && !isClosing) {
      setIsClosing(true);
      // ë‹«í˜ ì• ë‹ˆë©”ì´ì…˜ ì™„ë£Œ í›„ ëª¨ë‹¬ ìˆ¨ê¹€
      const timer = setTimeout(() => {
        setIsVisible(false);
        onComplete?.();
      }, 500); // í˜ì´ë“œì•„ì›ƒ ì• ë‹ˆë©”ì´ì…˜ ì‹œê°„
      return () => clearTimeout(timer);
    }
  }, [isLoading, isClosing, onComplete]);

  // ğŸ¯ ëª¨ë‹¬ì´ ë³´ì´ì§€ ì•Šìœ¼ë©´ ë Œë”ë§í•˜ì§€ ì•ŠìŒ
  if (!isVisible) return null;

  return (
    <div
      data-slot="splash-modal"
      className={cn(
        // ê¸°ë³¸ ë ˆì´ì•„ì›ƒ
        "fixed inset-0 z-50 flex flex-col items-center justify-center",
        // ë°°ê²½
        "bg-background",
        // ë‹«í˜ ì• ë‹ˆë©”ì´ì…˜
        isClosing && "animate-[splash-exit_0.5s_ease-in-out_forwards]",
        className,
      )}
      role="dialog"
      aria-modal="true"
      aria-label="ì•± ë¡œë”© ì¤‘"
    >
      {/* ë¡œê³  ì˜ì—­ */}
      {logo && (
        <div className="animate-[splash-logo_0.6s_ease-out_forwards] opacity-0">
          {logo}
        </div>
      )}

      {/* ë¸Œëœë“œëª… */}
      <h1 className="text-foreground mt-6 text-4xl font-bold tracking-tight">
        <AnimatedText text={brandName} startDelay={400} />
      </h1>

      {/* íƒœê·¸ë¼ì¸ */}
      {tagline && (
        <p
          className="text-muted-foreground mt-2 animate-[splash-tagline_0.4s_ease-out_forwards] text-lg opacity-0"
          style={{ animationDelay: "800ms" }}
        >
          {tagline}
        </p>
      )}

      {/* í”„ë¡œê·¸ë ˆìŠ¤ ë°” */}
      {progress !== undefined && (
        <div
          className="mt-8 w-64 animate-[splash-tagline_0.4s_ease-out_forwards] opacity-0"
          style={{ animationDelay: "1000ms" }}
        >
          <div className="bg-muted h-1.5 w-full overflow-hidden rounded-full">
            <div
              className="bg-primary h-full rounded-full transition-all duration-300 ease-out"
              style={{ width: `${Math.min(100, Math.max(0, progress))}%` }}
            />
          </div>
          <p className="text-muted-foreground mt-2 text-center text-sm">
            {Math.round(progress)}%
          </p>
        </div>
      )}

      {/* ë¡œë”© ìŠ¤í”¼ë„ˆ (í”„ë¡œê·¸ë ˆìŠ¤ê°€ ì—†ì„ ë•Œ) */}
      {progress === undefined && (
        <div
          className="mt-8 animate-[splash-tagline_0.4s_ease-out_forwards] opacity-0"
          style={{ animationDelay: "1000ms" }}
        >
          <div className="border-primary/30 border-t-primary size-8 animate-spin rounded-full border-2" />
        </div>
      )}
    </div>
  );
}

